    private void execute() {
        
        ImageIcon iconMaster = new ImageIcon(getClass().getResource("/image/master.png"));
        ImageIcon iconBarang = new ImageIcon(getClass().getResource("/image/barang.png"));
        
        MenuItem menuBarang = new MenuItem(null, true, iconBarang, "Item", null);
        MenuItem menuBarang2 = new MenuItem(null, true, iconBarang, "Item", null);
        MenuItem menuBarang3 = new MenuItem(null, true, iconBarang, "Item", null);
        MenuItem menuMaster = new MenuItem(iconMaster, false, null, "Master", null, menuBarang, menuBarang2, menuBarang3);
        menuMaster.setBorder(new EmptyBorder(10,10,10,10));
        
        addMenu(menuMaster);
    }
    private void addMenu(MenuItem... menu){
        for(int i =0; i < menu.length; i++){
            listMenu.add(menu[i]);
            ArrayList<MenuItem> subMenu = menu[i].getSubMenu();
            for(MenuItem item : subMenu){
                addMenu(item);
            }
        }
        listMenu.revalidate();
    }
